import styled from "styled-components";

export const DivForm = styled.div`
  margin: 0 auto;
  border: solid;
  border-radius: 10px;

  display: flex;
  flex-direction: column;

  width: 22%;
  height: 502px;

  background-color: #212529;
  color: white;

  h2 {
    margin-left: 40%;
    margin-top: 16%;
  }

  button {
    margin-left: 4.8%;
    margin-top: 7%;
    width: 90%;
    height: 48px;
    background-color: #ff577f;
    border: none;
    border-radius: 7px;
    color: white;
    font-size: 16px;
    cursor: pointer;

    &:hover {
      filter: brightness(0.9);
    }
  }

  .fazerCadastro {
    background-color: #868e96;
  }

  p {
    margin-left: 20%;
    color: #868e96;
  }
`;
