import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const schema = yup.object({
  name: yup.string().required(),
  email: yup.string().required(),
  password: yup.string().required(),
  confirmPassword: yup.string().required(),
  bio: yup.string().required(),
  contact: yup.string().required(),
  module: yup.string().required(),
});

const SigIn = () => {
  const { register, handleSubmit } = useForm({
    resolver: yupResolver(schema),
  });

  function registerUser(data) {
    console.log(data);
  }

  return (
    <form onSubmit={handleSubmit(registerUser)}>
      <label htmlFor="name">Nome:</label>
      <input type="text" id="name" {...register("name")} />

      <label htmlFor="email">Email:</label>
      <input type="text" id="email" {...register("email")} />

      <label htmlFor="password">Senha:</label>
      <input type="password" id="password" {...register("password")} />

      <label htmlFor="confirmPassword">Confirmar Senha:</label>
      <input
        type="password"
        id="confirmPassword"
        {...register("confirmPassword")}
      />

      <label htmlFor="bio">Bio:</label>
      <input type="text" id="bio" {...register("bio")} />

      <label htmlFor="contact">Contato:</label>
      <input type="number" id="contact" {...register("contact")} />

      <label htmlFor="module">Selecioanr Modulo:</label>
      <select id="module" {...register("module")}>
        <option value="Modulo1">Primeiro Modulo</option>
        <option value="Modulo2">Segundo Modulo</option>
        <option value="Modulo3">Terceiro Modulo</option>
        <option value="Modulo4">Quarto Modulo</option>
        <option value="Modulo5">Quinto Modulo</option>
        <option value="Modulo6">Sexto Modulo</option>
      </select>
      <button>Cadastrar</button>
    </form>
  );
};

export default SigIn;
