import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { useNavigate } from "react-router-dom";
import schema from "../../validators/registerUser";
import api from "../../requests/api";

import { DivForm } from "./styles";
import { Input } from "../../components/inputs";
import { Label } from "../../components/label";
import { Form } from "../../components/form";
import { Button } from "../../components/button";
import { GlobalDiv } from "../../components/globalDiv";

import logo from "../../assets/logo.svg";
import { toast } from "react-toastify";

const SigIn = () => {
  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  function registerUser(data) {
    console.log(data);
    api
      .post("/users", data)
      .then((res) => {
        toast.success("Usuario cadastrado com sucesso");
        console.log(res.data);
        navigate("/", { replace: true });
      })
      .catch((err) => {
        console.log(err);
        toast.error("Ops, algo deu errado");
      });
  }

  return (
    <GlobalDiv>
      <img src={logo} alt="Kenzie Hub Logo" />

      <h2>Crie sua conta</h2>
      <span>Rapido e gratis, vamos nessa</span>
      <button className="back" onClick={() => navigate("/", { replace: true })}>
        Voltar
      </button>
      <Form onSubmit={handleSubmit(registerUser)}>
        <Label htmlFor="name">Nome</Label>
        <Input type="text" id="name" {...register("name")} />
        <p className="error">{errors.name?.message}</p>

        <Label htmlFor="email">Email</Label>
        <Input type="text" id="email" {...register("email")} />
        <p className="error">{errors.email?.message}</p>

        <Label htmlFor="password">Senha</Label>
        <Input type="password" id="password" {...register("password")} />
        <p className="error">{errors.password?.message}</p>

        <Label htmlFor="confirmPassword">Confirmar Senha</Label>
        <Input
          type="password"
          id="confirmPassword"
          {...register("confirmPassword")}
        />
        <p className="error">{errors.confirmPassword?.message}</p>

        <Label htmlFor="bio">Bio</Label>
        <Input type="text" id="bio" {...register("bio")} />
        <p className="error">{errors.bio?.message}</p>

        <Label htmlFor="contact">Contato</Label>
        <Input type="number" id="contact" {...register("contact")} />
        <p className="error">{errors.contact?.message}</p>

        <Label htmlFor="course_module">Selecioanr Modulo</Label>
        <select id="course_module" {...register("course_module")}>
          <option value="Primeiro módulo (Introdução ao Frontend)">
            Primeiro módulo (Introdução ao Frontend)
          </option>
          <option value="Segundo módulo (Frontend Avançado)">
            Segundo módulo (Frontend Avançado)
          </option>
          <option value="Terceiro módulo (Introdução ao Backend)">
            Terceiro módulo (Introdução ao Backend)
          </option>
          <option value="Quarto módulo (Backend Avançado)">
            Quarto módulo (Backend Avançado)
          </option>
        </select>
        <Button>Cadastrar</Button>
      </Form>
    </GlobalDiv>
  );
};

export default SigIn;
