import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useEffect } from "react";
import api from "../../requests/api";

const schema = yup.object({
  name: yup.string().required("Nome e obrigatorio"),
  email: yup
    .string()
    .email("Deve ser um email")
    .required("Email e obrigatorio"),
  password: yup
    .string()
    .min(6, "No minimo 6 caracteres")
    .required("Senha e obrigatorio"),
  confirmPassword: yup
    .string()
    .oneOf([yup.ref("password")], "Necessario ser igual a senha"),
  bio: yup.string().required("Bio e obrigatorio"),
  contact: yup.string().required("Contato e obrigatorio"),
  module: yup.string().required("Modulo e obrigatorio"),
});

const SigIn = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  function registerUser(data) {
    return data;
  }
  useEffect(() => {
    api
      .post("/users", {
        email: registerUser.email,
        password: registerUser.password,
        name: registerUser.name,
        bio: registerUser.bio,
        contact: registerUser.contact,
        course_module: registerUser.module,
      })
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  }, [registerUser]);

  console.log(registerUser);

  return (
    <form onSubmit={handleSubmit(registerUser)}>
      <label htmlFor="name">Nome:</label>
      <input type="text" id="name" {...register("name")} />
      <p>{errors.name?.message}</p>

      <label htmlFor="email">Email:</label>
      <input type="text" id="email" {...register("email")} />
      <p>{errors.email?.message}</p>

      <label htmlFor="password">Senha:</label>
      <input type="password" id="password" {...register("password")} />
      <p>{errors.password?.message}</p>

      <label htmlFor="confirmPassword">Confirmar Senha:</label>
      <input
        type="password"
        id="confirmPassword"
        {...register("confirmPassword")}
      />
      <p>{errors.confirmPassword?.message}</p>

      <label htmlFor="bio">Bio:</label>
      <input type="text" id="bio" {...register("bio")} />
      <p>{errors.bio?.message}</p>

      <label htmlFor="contact">Contato:</label>
      <input type="number" id="contact" {...register("contact")} />
      <p>{errors.contact?.message}</p>

      <label htmlFor="module">Selecioanr Modulo:</label>
      <select id="module" {...register("module")}>
        <option value="Modulo1">Primeiro Modulo</option>
        <option value="Modulo2">Segundo Modulo</option>
        <option value="Modulo3">Terceiro Modulo</option>
        <option value="Modulo4">Quarto Modulo</option>
        <option value="Modulo5">Quinto Modulo</option>
        <option value="Modulo6">Sexto Modulo</option>
      </select>
      <button>Cadastrar</button>
    </form>
  );
};

export default SigIn;
